sql.where:
 AND setId  =#{setId} 
 AND defId  =#{defId} 
 AND nodeName  LIKE #{nodeName}  
 AND actDefId  =#{actDefId} 
 AND nodeId  LIKE #{nodeId}  
 AND formType  =#{formType} 
 AND formUrl  LIKE #{formUrl}  
 AND formKey  =#{formKey} 
 AND formDefName  =#{formDefName} 
AND setType =0

mapper.select:
SELECT FROM BPM_NODE_SET
order by ${orderField} ${orderSeq}
order by nodeOrder desc

SELECT *
FROM BPM_NODE_SET
WHERE actDefId=#{actDefId} and nodeId=#{nodeId}

SELECT distinct formKey,actDefId,formDefName
FROM bpm_node_set
WHERE formKey=#{formKey}
